sys_script_client.script:
//onLoad default value for this field will trigger the onChange function
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	//the below if statement will not bypass the onChange as isLoading method is taken out
	//isLoad: boolean value indicating whether the change is occurring as part of a form load. Value is true if change is due to a form load.
		if(newValue ===''){
			return;
		// If there is no change -- newValue is empty do not run the rest of the script below
	    // note The -if statement- in the default onChange Client Script template is modified
		// not bypass onchange when from isloading but to run after changes are made from onLoad
	    // method
		}

		var whatneeded = g_form.getValue('u_what_needed');

		// Clear all of the choices from the What needed field choice list
		g_form.clearOptions('u_what_needed');

		// If the the value of the Request type field is hr, add
		// two hr choices and other to the What needed field choice list
		if(newValue == 'hr'){
			g_form.addOption('u_what_needed', 'hr1', 'Human Resources 1' );
			g_form.addOption('u_what_needed','hr2','Human Resources 2');
			g_form.addOption('u_what_needed','other','Other');
		}
		// If the the value of the Request type field is facilities, add
		// two facilities choices and other to the What needed field
		// choice list
		if(newValue == 'facilities'){
			g_form.addOption('u_what_needed','facilities1','Facilities 1');
			g_form.addOption('u_what_needed','facilities2','Facilities 2');
			g_form.addOption('u_what_needed','other','Other');
		}
		// If the the value of the Request type field is legal, add
		// two legal choices and other to the What needed field
		// choice list
		if(newValue == 'legal'){
			g_form.addOption('u_what_needed','legal1','Legal 1');
			g_form.addOption('u_what_needed','legal2','Legal 2');
			g_form.addOption('u_what_needed','other','Other');
		}
	// If the form is loading and it is not a new record... 
	//because the above onchange methods will set existing records back to the options 
	//above for the What needed field so existing value will be over written
	//set the u_what_needed value to the value from the record before it was loaded
		if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', whatneeded); // whatneeded variable is saved above
		}
	}
